import React, { useState } from 'react';
import { Button } from '@mui/material';
import { saveAs } from 'file-saver';

// Define the interface for the data
interface PhoneNumberDetails {
    isValid: boolean;
    isPossible: boolean;
    phoneType: string;
    internationalFormat: string;
}

const DownloadResults = () => {
    // State to hold phone number details
    const [phoneNumberDetails, setPhoneNumberDetails] = useState<PhoneNumberDetails>();

    const downloadCSV = () => {
        let csvContent = "data:text/csv;charset=utf-8,";

        // CSV Headers
        const headers = "IsValid,IsPossible,PhoneType,InternationalFormat\n";
        csvContent += headers;

        // Check if phone number details are available
        if (phoneNumberDetails) {
            const csvRow = [
                phoneNumberDetails.isValid,
                phoneNumberDetails.isPossible,
                phoneNumberDetails.phoneType,
                phoneNumberDetails.internationalFormat
            ].join(",");
            csvContent += csvRow + "\n";
        }

        // Create a Blob and trigger the download
        const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
        saveAs(blob, "phoneNumberDetails.csv");
    };

    return (
        <div>
            <Button variant="contained" color="primary" onClick={downloadCSV}>
                Download CSV
            </Button>
        </div>
    );
}

export default DownloadResults;
