import React, { useState } from 'react';
import { Button } from '@mui/material';
import { saveAs } from 'file-saver';

// Define the interface for the data
interface ValidationData {
    phoneNumber: string;
    isValid: boolean;
}

const DownloadResults = () => {
    // Now TypeScript knows what type of data the array will hold
    const [data, setData] = useState<ValidationData[]>([]);

    const downloadCSV = () => {
        let csvContent = "data:text/csv;charset=utf-8,";

        // Add your CSV header
        const headers = "Phone Number, IsValid, IsPossible, PhoneType, InternationalFormat\n";
        csvContent += headers;

        // Add the rows
        data.forEach(row => {
            const csvRow = [row.phoneNumber, row.isValid].join(",");
            csvContent += csvRow + "\n";
        });

        // Create a Blob and trigger the download
        const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
        saveAs(blob, "validationResults.csv");
    };

    return (
        <div>
            <Button variant="contained" color="primary" onClick={downloadCSV}>
                Download CSV
            </Button>
        </div>
    );
}

export default DownloadResults;
